mapper.update:
UPDATE users
   SET user_password = #{userPassword}
      ,user_name = #{userName}
      ,user_phone = #{userPhone}
      ,user_address = #{userAddress}
      ,user_birth = #{userBirth}
      ,user_upd_date = now()
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM users
WHERE user_no = #{userNo}
  AND user_id = #{userId}

mapper.update:
UPDATE users
SET status = 1
WHERE user_no = #{userNo}
