mapper.select:
SELECT *
FROM files
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
  AND file_code = 0
ORDER BY file_reg_date DESC

mapper.update:
UPDATE files
   SET parent_table = #{parentTable}
      ,parent_no = #{parentNo}
      ,file_name = #{fileName}
      ,origin_file_name = #{originFileName}
      ,file_path = #{filePath}
      ,file_size = #{fileSize}
      ,file_code = #{fileCode}
      ,file_upd_date = now()
WHERE file_no = #{fileNo}

mapper.delete:
DELETE FROM files
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
