mapper.select:
SELECT s.*, f.*, u.user_name FROM joeun.service s LEFT JOIN ( SELECT * FROM joeun.files WHERE parent_table = 'service' AND file_code = 1 ) f ON s.service_no = f.parent_no
LEFT JOIN joeun.users u ON s.partner_no = u.user_no
WHERE s.service_name LIKE CONCAT('%', #{option.keyword}, '%') ORDER BY s.service_no DESC LIMIT #{page.index}, #{page.rows};
