mapper.update:
UPDATE board
   SET board_title = #{boardTitle}
      ,board_content = #{boardContent}
      ,board_upd_date = now()
WHERE board_no = #{boardNo}

UPDATE board
   SET board_views = board_views + 1
WHERE board_no = #{boardNo}

UPDATE board
SET board_like = board_like + 1
WHERE board_no = #{boardNo}
