mapper.update:
UPDATE files
SET parent_table = #{parentTable}, parent_no = #{parentNo}, file_name = #{fileName}, origin_file_name = #{originFileName},
    file_path = #{filePath}, file_size = #{fileSize}, file_code = #{fileCode}
WHERE file_no = #{fileNo}

mapper.select:
SELECT * FROM files
WHERE parent_no = #{parentNo}
  AND parent_table = #{parentTable}
ORDER BY reg_date DESC

mapper.delete:
DELETE FROM files
WHERE parent_no = #{parentNo}
  AND parent_table = #{parentTable}
